    private void btnVerificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVerificarActionPerformed
        // TODO add your handling code here:
        int v = Integer.parseInt(txtValor.getText());
        if (v%2==0){
            lblResultado.setText("<html>" + "O número " + v + " é um número " + "<strong>PAR<strong>" + "<html>");
        } else {
            lblResultado.setText("<html>" + "O número " + v + " é um número " + "<strong>IMPAR<strong>" + "<html>");
            //utilzação de comandos <hmtl> para quebrar as linhas e <strong> para colocar en negrito.
        }
    }//GEN-LAST:event_btnVerificarActionPerformed

